import React, { useState } from 'react';
import Slider from 'react-slick';
import { Box, Typography } from '@mui/material';

const ImageSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    afterChange: (current) => setCurrentSlide(current), // Update the current slide index
  };

  const slides = [
    {
      image: 'https://cloudinary.hbs.edu/hbsit/image/upload/s--O0PXWnT3--/f_auto,c_fill,h_375,w_750,/v20200101/BDD0688FF02068E5C427B0954F8A2297.jpg',
      text: 'Harvard Business School',
    },
    {
      image: 'https://scontent.fcrk3-2.fna.fbcdn.net/v/t39.30808-6/425775653_122100603146207557_477926010671292945_n.jpg?_nc_cat=101&ccb=1-7&_nc_sid=cc71e4&_nc_eui2=AeGgAztwtFpfN4Qawka5DWneRt6-e9ntUodG3r572e1Sh9xJJItHQsZiTGQZzgLorSTBy6nlexOizVpEC11e-PzZ&_nc_ohc=hAturPziC_wQ7kNvgGxTQou&_nc_ht=scontent.fcrk3-2.fna&oh=00_AYCAUCowbzhQFMAUyMWEyNpDPZeNVvrT695vzvR69Zypqw&oe=669D055A',
      text: 'Business Meeting',
    },
    {
      image: 'https://www.cazbah.net/wp-content/uploads/2009/03/agenda-analysis-business-990818-768x468.jpg',
      text: 'Business Analysis',
    },
  ];

  return (
    <Box sx={{ width: '80%', margin: 'auto' }}>
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <Box key={index}>
            <img src={slide.image} alt={`Slide ${index + 1}`} style={{ width: '100%', borderRadius: '10px' }} />
          </Box>
        ))}
      </Slider>
      <Typography variant="h6" align="center" sx={{ mt: 2 }}>
        {slides[currentSlide].text}
      </Typography>
    </Box>
  );
};

export default ImageSlider;
